import React, { useState } from "react";

const Navigation: React.FC = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  // Toggle dark mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle("dark", !darkMode);
  };

  return (
    <nav className={`w-full px-4 py-3 shadow ${darkMode ? "bg-gray-900" : "bg-gray-800"} transition-colors`}>
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center">
          <span className="text-white font-bold text-2xl mr-2">F</span>
          <span className="text-white font-bold text-xl">FASHION</span>
        </div>
        {/* Desktop Menu */}
        <ul className="hidden md:flex space-x-8 items-center">
          <li className="text-white hover:text-yellow-300 cursor-pointer">CATALOGUE</li>
          <li className="text-white hover:text-yellow-300 cursor-pointer">FASHION</li>
          <li className="text-white hover:text-yellow-300 cursor-pointer">FAVOURITE</li>
          <li className="text-white hover:text-yellow-300 cursor-pointer">LIFESTYLE</li>
          <li>
            <button
              aria-label="Toggle Dark Mode"
              onClick={toggleDarkMode}
              className="text-white hover:text-yellow-300 text-xl focus:outline-none"
            >
              {darkMode ? "ðŸŒ™" : "ðŸŒž"}
            </button>
          </li>
          <li>
            <button className="bg-black text-white px-4 py-2 rounded hover:bg-yellow-400 hover:text-black transition">
              SIGN UP
            </button>
          </li>
        </ul>
        {/* Mobile Hamburger */}
        <div className="md:hidden flex items-center">
          <button
            onClick={() => setMenuOpen(!menuOpen)}
            className="text-white focus:outline-none"
            aria-label="Open Menu"
          >
            {menuOpen ? (
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            ) : (
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            )}
          </button>
        </div>
      </div>
      {/* Mobile Menu */}
      {menuOpen && (
        <ul className={`md:hidden mt-4 space-y-3 ${darkMode ? "bg-gray-900" : "bg-gray-800"} p-4 rounded`}>
          <li className="text-white hover:text-yellow-300 cursor-pointer">CATALOGUE</li>
          <li className="text-white hover:text-yellow-300 cursor-pointer">FASHION</li>
          <li className="text-white hover:text-yellow-300 cursor-pointer">FAVOURITE</li>
          <li className="text-white hover:text-yellow-300 cursor-pointer">LIFESTYLE</li>
          <li>
            <button
              aria-label="Toggle Dark Mode"
              onClick={toggleDarkMode}
              className="text-white hover:text-yellow-300 text-xl focus:outline-none"
            >
              {darkMode ? "ðŸŒ™" : "ðŸŒž"}
            </button>
          </li>
          <li>
            <button className="bg-black text-white px-4 py-2 rounded hover:bg-yellow-400 hover:text-black transition">
              SIGN UP
            </button>
          </li>
        </ul>
      )}
    </nav>
  );
};

export default Navigation;
