import React, { useState } from "react";
import "./Sidebar.css";
import logo from "../workspaces/StyleCart/stylecart-frontend/src/assets/logo.svg"; // or .svg

interface SidebarProps {
  open: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ open, onClose }) => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <aside className={`sidebar${open ? " open" : ""}`}>
      <button
        className="sidebar-close"
        onClick={onClose}
        aria-label="Close Sidebar"
      >
        &times;
      </button>
      <div className="sidebar-logo">
        <img src={logo} alt="StyleCart" style={{ width: 48, height: 48 }} />
        <div>StyleCart</div>
      </div>
      <nav className="sidebar-nav">
        <a href="/">Catalogue</a>
        <a href="/">Fashion</a>
        <a href="/">Favourite</a>
        <a href="/">Lifestyle</a>
      </nav>
    </aside>
  );
};

export default Sidebar;
