import React, { useState } from 'react';

const menuItems = [
  { label: 'Products', dropdown: ['Best Practices', 'Overview', 'Books', 'Podcast', 'Conversations'] },
  { label: 'Education', dropdown: ['Best Practices', 'Overview', 'Books', 'Podcast', 'Conversations'] },
  { label: 'Enterprise' },
  { label: 'Blog' },
  { label: 'Contact us' },
];

const Navbar: React.FC = () => {
  const [openDropdown, setOpenDropdown] = useState<string | null>(null);

  return (
    <nav style={{
      background: '#23262f',
      display: 'flex',
      alignItems: 'center',
      padding: '0 2rem',
      height: '64px',
      fontFamily: 'Montserrat, Arial, sans-serif',
      position: 'relative',
      zIndex: 100,
    }}>
      {/* Logo */}
      <div style={{ marginRight: '2rem', fontWeight: 700, fontSize: '2rem', letterSpacing: 2, background: 'linear-gradient(90deg, #00eaff, #a259f7)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
        Style Cart
      </div>
      {/* Navigation */}
      <div style={{ display: 'flex', alignItems: 'center', flex: 1 }}>
        {menuItems.map((item) => (
          <div
            key={item.label}
            style={{ position: 'relative', margin: '0 1rem' }}
            onMouseEnter={() => item.dropdown && setOpenDropdown(item.label)}
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <span style={{
              color: '#fff',
              fontWeight: 500,
              fontSize: '1.1rem',
              cursor: 'pointer',
              padding: '0.5rem 0.75rem',
              borderRadius: 4,
              transition: 'background 0.2s',
              background: openDropdown === item.label ? '#2d2f3a' : 'none'
            }}>
              {item.label} {item.dropdown && <span style={{ fontSize: '0.8em' }}>â–¼</span>}
            </span>
            {/* Dropdown */}
            {item.dropdown && openDropdown === item.label && (
              <div style={{
                position: 'absolute',
                top: '100%',
                left: 0,
                background: '#fff',
                color: '#23262f',
                minWidth: 180,
                boxShadow: '0 4px 16px rgba(0,0,0,0.12)',
                borderRadius: 6,
                marginTop: 8,
                overflow: 'hidden'
              }}>
                {item.dropdown.map((sub, idx) => (
                  <div
                    key={sub}
                    style={{
                      padding: '0.75rem 1.25rem',
                      background: idx === 1 ? '#7ffcff' : 'none', // Highlight 'Overview' as in image
                      fontWeight: idx === 1 ? 600 : 400,
                      cursor: 'pointer',
                      transition: 'background 0.2s'
                    }}
                    onMouseEnter={e => (e.currentTarget.style.background = '#7ffcff')}
                    onMouseLeave={e => (e.currentTarget.style.background = idx === 1 ? '#7ffcff' : 'none')}
                  >
                    {sub}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
      {/* Search Icon */}
      <div style={{ color: '#fff', fontSize: '1.5rem', marginLeft: '2rem', cursor: 'pointer' }}>
        <svg width="24" height="24" fill="none" stroke="currentColor" strokeWidth="2"><circle cx="11" cy="11" r="8"/><line x1="21" y1="21" x2="16.65" y2="16.65"/></svg>
      </div>
    </nav>
  );
};

export default Navbar;
